SOURCE:
static Expr *create(std::string string){
	Expr *expr = new Expr(global_String);
	expr->data = new StringData(string);
	expr->next = nullptr;
	expr->previous = nullptr;
	expr->child = nullptr;
	expr->parent = nullptr;
	return expr;
}

DATAINC:
struct StringData : Data{
	std::string string;
	StringData();
	StringData(std::string);
	~StringData();
};

DATASRC:
StringData::StringData(){
	this->string = "";
}
StringData::StringData(std::string){
	this->string = string;
}
StringData::~StringData(){}
